function StartupInquiriesTab() {
  const { data: leads, isLoading } = useQuery({
    queryKey: ["/api/admin/leads"],
    queryFn: async () => {
      const res = await fetch("/api/admin/leads");
      if (!res.ok) throw new Error("Failed to fetch leads");
      return res.json();
    },
  });

  const startupInquiries = leads?.filter((lead: any) => lead.kind === "startup") || [];

  if (isLoading) {
    return (
      <Card>
        <CardContent className="p-8 text-center">
          <div className="w-8 h-8 border-4 border-primary border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-muted-foreground">Loading startup inquiries...</p>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <Rocket className="h-5 w-5" />
          Startup Inquiries
        </CardTitle>
        <CardDescription>Student startup launch inquiries and contact submissions ({startupInquiries.length} total)</CardDescription>
      </CardHeader>
      <CardContent>
        <div className="space-y-4">
          {startupInquiries.length === 0 ? (
            <div className="text-center py-12">
              <Rocket className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
              <p className="text-muted-foreground">No startup inquiries yet</p>
              <p className="text-sm text-muted-foreground">When students submit startup launch forms, they will appear here</p>
            </div>
          ) : (
            <div className="space-y-3">
              {startupInquiries.map((inquiry: any) => (
                <div key={inquiry.id} className="border rounded-lg p-4 hover:bg-muted/50 transition-colors">
                  <div className="flex justify-between items-start mb-2">
                    <div>
                      <h4 className="font-semibold">{inquiry.name}</h4>
                      <p className="text-sm text-muted-foreground">{inquiry.email} • {inquiry.phone}</p>
                    </div>
                    <Badge variant="outline" className="bg-purple-50 text-purple-700 border-purple-200">
                      <Rocket className="h-3 w-3 mr-1" />
                      {inquiry.metadata?.stage || "Inquiry"}
                    </Badge>
                  </div>
                  <p className="text-sm mb-3 text-foreground">{inquiry.message}</p>
                  <div className="flex flex-wrap gap-3 text-xs text-muted-foreground">
                    <span className="flex items-center gap-1">
                      <IndianRupee className="h-3 w-3" />
                      Funding Needed: ₹{inquiry.metadata?.fundingNeeded || "N/A"}
                    </span>
                    <span>Submitted: {format(new Date(inquiry.createdAt), "MMM dd, yyyy")}</span>
                  </div>
                  <div className="flex gap-2 mt-3">
                    <a
                      href={`mailto:${inquiry.email}`}
                      className="text-xs px-3 py-1.5 bg-primary text-primary-foreground rounded-md hover:bg-primary/90 transition-colors"
                    >
                      Email
                    </a>
                    <a
                      href={`tel:${inquiry.phone}`}
                      className="text-xs px-3 py-1.5 border rounded-md hover:bg-muted transition-colors"
                    >
                      Call
                    </a>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

export default function AdminDashboard() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [analytics, setAnalytics] = useState<Analytics | null>(null);
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);
  const [activeTab, setActiveTab] = useState("overview");
  const [showWelcome, setShowWelcome] = useState(() => {
    return localStorage.getItem("hideAdminWelcome") !== "true";
  });
  const [filters, setFilters] = useState<Record<string, any>>({});
  const [services, setServices] = useState<any[]>([]);
  const [categories, setCategories] = useState<any[]>([]);
  const [systemHealth, setSystemHealth] = useState<any>(null);
  const [healthLoading, setHealthLoading] = useState(false);
  const [timeRange, setTimeRange] = useState<'7d' | '30d' | '90d'>('7d');

  useEffect(() => {
    loadAllData();
  }, []);

  useEffect(() => {
    if (activeTab === 'settings') {
      checkSystemHealth();
    }
  }, [activeTab]);

  const loadAllData = async () => {
    setLoading(true);
    await Promise.all([
      loadAnalytics(),
      loadServices(),
      loadCategories(),
    ]);
    setLoading(false);
  };

  const handleRefresh = async () => {
    setRefreshing(true);
    await loadAllData();
    setRefreshing(false);
    toast({
      title: "Dashboard Refreshed",
      description: "All data has been updated",
    });
  };

  const loadAnalytics = async () => {
    try {
      const response = await fetch("/api/admin/analytics");
      if (response.ok) {
        const data = await response.json();
        setAnalytics(data);
      }
    } catch (err) {
      console.error("Failed to load analytics", err);
    }
  };

  const loadServices = async () => {
    try {
      const response = await fetch("/api/admin/services");
      if (response.ok) {
        const data = await response.json();
        setServices(data);
      }
    } catch (err) {
      console.error("Failed to load services", err);
    }
  };

  const loadCategories = async () => {
    try {
      const response = await fetch("/api/admin/categories");
      if (response.ok) {
        const data = await response.json();
        setCategories(data);
      }
    } catch (err) {
      console.error("Failed to load categories", err);
    }
  };

  const checkSystemHealth = async () => {
    try {
      setHealthLoading(true);
      const res = await fetch("/api/admin/integration-status");
      if (res.ok) {
        const data = await res.json();
        setSystemHealth(data);
        toast({
          title: "System Check Complete",
          description: "All system status updated",
        });
      }
    } catch (error) {
      toast({
        title: "Health Check Failed",
        description: "Unable to fetch system health status",
        variant: "destructive",
      });
    } finally {
      setHealthLoading(false);
    }
  };

  const handleLogout = async () => {
    try {
      await fetch("/api/admin/logout", { method: "POST" });
      sessionStorage.clear();
      toast({
        title: "Logged Out",
        description: "You have been logged out successfully",
      });
      setLocation("/admin/login");
    } catch (err) {
      console.error("Logout failed", err);
    }
  };

  const handleDismissWelcome = () => {
    localStorage.setItem("hideAdminWelcome", "true");
    setShowWelcome(false);
  };

  const handleQuickAction = (action: string) => {
    switch (action) {
      case "add-service":
        setActiveTab("services");
        toast({
          title: "Add Service",
          description: "Navigate to the Services tab to add a new service",
        });
        break;
      case "add-user":
        setActiveTab("users");
        break;
      case "export-orders":
        toast({
          title: "Exporting Orders",
          description: "Preparing CSV export...",
        });
        break;
      default:
        toast({
          title: "Action Triggered",
          description: `Quick action: ${action}`,
        });
    }
  };

  const handleApplyFilter = (newFilters: Record<string, any>) => {
    setFilters(newFilters);
    toast({
      title: "Filters Applied",
      description: "View updated with saved filter",
    });
  };

  const exportToCSV = async (type: 'orders' | 'services' | 'users' | 'analytics') => {
    try {
      if (!analytics && type === 'analytics') {
        toast({
          title: "Data Not Ready",
          description: "Please wait for analytics to load before exporting",
          variant: "destructive",
        });
        return;
      }

      let data: any[] = [];
      let filename = '';
      let headers: string[] = [];

      switch (type) {
        case 'orders':
          const ordersRes = await fetch('/api/admin/orders');
          if (ordersRes.ok) {
            data = await ordersRes.json();
            filename = `orders_${format(new Date(), 'yyyy-MM-dd')}.csv`;
            headers = ['ID', 'User', 'Total', 'Status', 'Date'];
          }
          break;
        case 'services':
          data = services;
          filename = `services_${format(new Date(), 'yyyy-MM-dd')}.csv`;
          headers = ['ID', 'Name', 'Category', 'Price (INR)', 'Active'];
          break;
        case 'users':
          const usersRes = await fetch('/api/admin/users');
          if (usersRes.ok) {
            data = await usersRes.json();
            filename = `users_${format(new Date(), 'yyyy-MM-dd')}.csv`;
            headers = ['ID', 'Email', 'Name', 'User Type', 'Created At'];
          }
          break;
        case 'analytics':
          data = [analytics];
          filename = `analytics_${format(new Date(), 'yyyy-MM-dd')}.csv`;
          headers = ['Total Revenue', 'Total Orders', 'Pending Orders', 'Completed Orders', 'Total Leads', 'Active Services'];
          break;
      }

      if (!data || data.length === 0) {
        toast({
          title: "No Data",
          description: "There is no data to export",
          variant: "destructive",
        });
        return;
      }

      const csvContent = [
        headers.join(','),
        ...data.map(row => {
          if (type === 'orders') {
            return [row.id, row.userId, row.totalInr, row.status, format(new Date(row.createdAt), 'yyyy-MM-dd')].join(',');
          } else if (type === 'services') {
            return [row.id, row.name, row.categoryId, row.basePriceInr, row.active].join(',');
          } else if (type === 'users') {
            return [row.id, row.email, row.fullName || '', row.userType, format(new Date(row.createdAt), 'yyyy-MM-dd')].join(',');
          } else {
            return [row.totalRevenue, row.totalOrders, row.pendingOrders, row.completedOrders, row.totalLeads, row.activeServices].join(',');
          }
        })
      ].join('\n');

      const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
      const link = document.createElement('a');
      const url = URL.createObjectURL(blob);
      link.setAttribute('href', url);
      link.setAttribute('download', filename);
      link.style.visibility = 'hidden';
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);

      toast({
        title: "Export Successful",
        description: `${filename} has been downloaded`,
      });
    } catch (err) {
      console.error('Export failed:', err);
      toast({
        title: "Export Failed",
        description: "Failed to export data",
        variant: "destructive",
      });
    }
