            </div>
            <div className="flex items-center gap-2" data-testid="text-trust-services">
              <CheckCircle2 className="h-4 w-4 text-primary" />
              <span>300+ verified services</span>
            </div>
          </motion.div>
        </div>
      </div>
    </div>
  );
}

