import { useState } from "react";
import { Link } from "wouter";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import Navigation from "@/components/Navigation";
import { CheckCircle2 } from "lucide-react";

const forgotPasswordSchema = z.object({
  email: z.string().email("Invalid email address"),
});

type ForgotPasswordData = z.infer<typeof forgotPasswordSchema>;

export default function ForgotPassword() {
  const { toast } = useToast();
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [emailSent, setEmailSent] = useState(false);

  const form = useForm<ForgotPasswordData>({
    resolver: zodResolver(forgotPasswordSchema),
    defaultValues: {
      email: "",
    },
  });

  const onSubmit = async (data: ForgotPasswordData) => {
    setIsSubmitting(true);
    console.log("Password reset requested for:", data.email);

    await new Promise((resolve) => setTimeout(resolve, 1000));

    setEmailSent(true);
    setIsSubmitting(false);
  };

  if (emailSent) {
    return (
      <div className="flex flex-col min-h-screen">
        <Navigation />
        <main className="flex-1 flex items-center justify-center py-16 px-4">
          <Card className="w-full max-w-md backdrop-blur-md bg-card/90 text-center">
            <CardContent className="pt-12 pb-8">
              <div className="inline-flex p-4 rounded-full bg-accent/10 mb-6">
                <CheckCircle2 className="h-12 w-12 text-accent" />
              </div>
              <h2 className="text-2xl font-heading font-bold mb-4">Check Your Email</h2>
              <p className="text-muted-foreground mb-8">
                We've sent password reset instructions to your email address.
              </p>
              <Button asChild className="w-full" data-testid="button-back-to-login">
                <Link href="/login">Back to Login</Link>
              </Button>
            </CardContent>
          </Card>
        </main>
      </div>
    );
  }

  return (
    <div className="flex flex-col min-h-screen">
      <Navigation />
      <main className="flex-1 flex items-center justify-center py-16 px-4">
        <Card className="w-full max-w-md backdrop-blur-md bg-card/90">
          <CardHeader className="text-center">
            <CardTitle className="text-2xl font-heading">Reset Password</CardTitle>
            <CardDescription>Enter your email to receive reset instructions</CardDescription>
          </CardHeader>
          <CardContent>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="email"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Email</FormLabel>
                      <FormControl>
                        <Input
                          type="email"
                          placeholder="your@email.com"
                          {...field}
                          data-testid="input-email"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <Button
                  type="submit"
                  className="w-full"
                  disabled={isSubmitting}
                  data-testid="button-submit"
                >
                  {isSubmitting ? "Sending..." : "Send Reset Link"}
                </Button>

                <p className="text-center text-sm text-muted-foreground mt-6">
                  Remember your password?{" "}
                  <Link href="/login" className="text-primary hover:underline" data-testid="link-login">
                    Sign in
                  </Link>
                </p>
              </form>
            </Form>
          </CardContent>
        </Card>
      </main>
    </div>
  );
}

pages/ResetPassword.tsx.content:
import { useState } from "react";
import { Link } from "wouter";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import Navigation from "@/components/Navigation";
import { supabase } from "@/lib/supabase-client";
import { ArrowLeft } from "lucide-react";

const resetSchema = z.object({
  email: z.string().email("Invalid email address"),
});

type ResetData = z.infer<typeof resetSchema>;

export default function ResetPassword() {
  const { toast } = useToast();
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [emailSent, setEmailSent] = useState(false);

  const form = useForm<ResetData>({
    resolver: zodResolver(resetSchema),
    defaultValues: {
      email: "",
    },
  });

  const onSubmit = async (data: ResetData) => {
    setIsSubmitting(true);

    try {
      const { error } = await supabase.auth.resetPasswordForEmail(data.email, {
        redirectTo: `${window.location.origin}/auth/update-password`,
      });

      if (error) throw error;

      setEmailSent(true);
      toast({
        title: "Email Sent!",
        description: "Check your email for a password reset link.",
      });
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to send reset email",
        variant: "destructive",
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="flex flex-col min-h-screen">
      <Navigation />
      <main className="flex-1 flex items-center justify-center py-16 px-4">
        <Card className="w-full max-w-md backdrop-blur-md bg-card/90">
          <CardHeader className="text-center">
            <CardTitle className="text-2xl font-heading">Reset Password</CardTitle>
            <CardDescription>
              {emailSent 
                ? "We've sent you an email with a password reset link" 
                : "Enter your email to receive a password reset link"}
            </CardDescription>
          </CardHeader>
          <CardContent>
            {!emailSent ? (
              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                  <FormField
                    control={form.control}
                    name="email"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Email</FormLabel>
                        <FormControl>
                          <Input
                            type="email"
                            placeholder="your@email.com"
                            {...field}
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <Button
                    type="submit"
                    className="w-full"
                    disabled={isSubmitting}
                  >
                    {isSubmitting ? "Sending..." : "Send Reset Link"}
                  </Button>

                  <div className="text-center">
                    <Link
                      href="/login"
                      className="text-sm text-primary hover:underline inline-flex items-center gap-2"
                    >
                      <ArrowLeft className="h-4 w-4" />
                      Back to login
                    </Link>
                  </div>
                </form>
              </Form>
            ) : (
              <div className="space-y-4 text-center">
                <p className="text-sm text-muted-foreground">
                  Check your email inbox for a password reset link. The link will expire in 1 hour.
                </p>
                <Button asChild className="w-full">
                  <Link href="/login">Return to Login</Link>
                </Button>
              </div>
            )}
          </CardContent>
        </Card>
      </main>
    </div>
  );
}

client/src/lib/auth-service.ts.content:
import { supabase } from "./supabase-client";
import { AuthError, Session, User } from "@supabase/supabase-js";

export interface AuthResult {
  success: boolean;
  error?: string;
  errorCode?: string;
  session?: Session | null;
  user?: User | null;
}

export interface UserMetadata {
  full_name?: string;
  phone?: string;
  role?: 'student' | 'business' | 'company' | 'admin';
}

// Unified sign-in function
export async function signIn(email: string, password: string): Promise<AuthResult> {
  try {
    const { data, error } = await supabase.auth.signInWithPassword({
      email,
      password,
    });

    if (error) {
      return {
        success: false,
        error: getReadableErrorMessage(error),
        errorCode: error.status?.toString() || 'unknown',
      };
    }

    return {
      success: true,
      session: data.session,
      user: data.user,
    };
  } catch (error: any) {
    return {
      success: false,
      error: 'Network error. Please check your connection.',
      errorCode: 'network_error',
    };
  }
}

// Unified sign-up function
export async function signUp(
  email: string,
  password: string,
  metadata?: UserMetadata
): Promise<AuthResult> {
  try {
    const { data, error } = await supabase.auth.signUp({
      email,
      password,
      options: {
        data: metadata || {},
        emailRedirectTo: `${window.location.origin}/profile`,
      },
    });

    if (error) {
      return {
        success: false,
        error: getReadableErrorMessage(error),
        errorCode: error.status?.toString() || 'unknown',
      };
    }

    // Check if email confirmation is required
    if (data.user && !data.session) {
      return {
        success: true,
        user: data.user,
        session: null,
        error: 'Please check your email to verify your account.',
        errorCode: 'email_verification_required',
      };
    }

    return {
      success: true,
      session: data.session,
      user: data.user,
    };
  } catch (error: any) {
    return {
      success: false,
      error: 'Network error. Please check your connection.',
      errorCode: 'network_error',
    };
  }
}

// Unified sign-out function
export async function signOut(): Promise<AuthResult> {
  try {
    const { error } = await supabase.auth.signOut();

    if (error) {
      return {
        success: false,
        error: 'Failed to sign out. Please try again.',
        errorCode: error.status?.toString() || 'unknown',
      };
    }

    return {
      success: true,
    };
  } catch (error: any) {
    return {
      success: false,
      error: 'Network error. Please try again.',
      errorCode: 'network_error',
    };
  }
}

// Get current session
export async function getCurrentSession(): Promise<{ session: Session | null; user: User | null }> {
  try {
    const { data } = await supabase.auth.getSession();
    return {
      session: data.session,
      user: data.session?.user || null,
    };
  } catch (error) {
    console.error('Failed to get current session:', error);
    return {
      session: null,
      user: null,
    };
  }
}

// Refresh session
export async function refreshSession(): Promise<AuthResult> {
  try {
    const { data, error } = await supabase.auth.refreshSession();

    if (error) {
      return {
        success: false,
        error: 'Session expired. Please log in again.',
        errorCode: 'session_expired',
      };
    }

    return {
      success: true,
      session: data.session,
      user: data.user,
    };
  } catch (error: any) {
    return {
      success: false,
      error: 'Failed to refresh session.',
      errorCode: 'refresh_failed',
    };
  }
}

// Check if user is admin
export async function isAdmin(): Promise<boolean> {
  try {
    const { data } = await supabase.auth.getSession();
    const user = data.session?.user;

    if (!user) return false;

    // Check user metadata for admin role
    const role = user.user_metadata?.role || user.app_metadata?.role;
    return role === 'admin';
  } catch (error) {
    console.error('Failed to check admin status:', error);
    return false;
  }
}

// Get user role
export async function getUserRole(): Promise<string | null> {
  try {
    const { data } = await supabase.auth.getSession();
    const user = data.session?.user;

    if (!user) return null;

    return user.user_metadata?.role || user.app_metadata?.role || 'user';
  } catch (error) {
    console.error('Failed to get user role:', error);
    return null;
  }
}

// Reset password request
export async function resetPassword(email: string): Promise<AuthResult> {
  try {
    const { error } = await supabase.auth.resetPasswordForEmail(email, {
      redirectTo: `${window.location.origin}/auth/reset-password`,
    });

    if (error) {
      return {
        success: false,
        error: getReadableErrorMessage(error),
        errorCode: error.status?.toString() || 'unknown',
      };
    }

    return {
      success: true,
    };
  } catch (error: any) {
    return {
      success: false,
      error: 'Network error. Please try again.',
      errorCode: 'network_error',
    };
  }
}

// Update password
export async function updatePassword(newPassword: string): Promise<AuthResult> {
  try {
    const { error } = await supabase.auth.updateUser({
      password: newPassword,
    });

    if (error) {
      return {
        success: false,
        error: getReadableErrorMessage(error),
        errorCode: error.status?.toString() || 'unknown',
      };
    }

    return {
      success: true,
    };
  } catch (error: any) {
    return {
      success: false,
      error: 'Failed to update password.',
      errorCode: 'update_failed',
    };
  }
}

// Resend verification email
export async function resendVerificationEmail(email: string): Promise<AuthResult> {
  try {
    const { error } = await supabase.auth.resend({
      type: 'signup',
      email,
    });

    if (error) {
      return {
        success: false,
        error: getReadableErrorMessage(error),
        errorCode: error.status?.toString() || 'unknown',
      };
    }

    return {
      success: true,
    };
  } catch (error: any) {
    return {
      success: false,
      error: 'Failed to resend verification email.',
      errorCode: 'resend_failed',
    };
  }
}

// Helper function to convert Supabase errors to readable messages
function getReadableErrorMessage(error: AuthError): string {
  const message = error.message.toLowerCase();

  if (message.includes('invalid login credentials')) {
    return 'Invalid email or password. Please check your credentials and try again.';
  }

  if (message.includes('email not confirmed')) {
    return 'Please verify your email address before logging in. Check your inbox for the verification link.';
  }

  if (message.includes('user not found')) {
    return 'No account found with this email address.';
  }

  if (message.includes('password')) {
    return 'Password must be at least 6 characters long.';
  }

  if (message.includes('rate limit')) {
    return 'Too many attempts. Please wait a moment before trying again.';
  }

  if (message.includes('network')) {
    return 'Network error. Please check your internet connection and try again.';
  }

  if (message.includes('already registered') || message.includes('user already registered')) {
    return 'An account with this email already exists. Please log in instead.';
  }

  // Return the original message if no specific case matches
  return error.message || 'An unexpected error occurred. Please try again.';
}

// Set up auth state change listener
export function onAuthStateChange(callback: (session: Session | null) => void) {
  const { data: { subscription } } = supabase.auth.onAuthStateChange((_event, session) => {
    callback(session);
  });

  return () => {
    subscription.unsubscribe();
  };
}

pages/Login.tsx.content:
import { useState, useEffect } from "react";
import { Link, useLocation } from "wouter";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Checkbox } from "@/components/ui/checkbox";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { useToast } from "@/hooks/use-toast";
import Navigation from "@/components/Navigation";
import { signIn, getCurrentSession, resendVerificationEmail } from "@/lib/auth-service";
import { Loader2, Eye, EyeOff, AlertCircle } from "lucide-react";

const loginSchema = z.object({
  email: z.string().email("Please enter a valid email address"),
  password: z.string().min(1, "Password is required"),
  rememberMe: z.boolean().default(false),
});

type LoginData = z.infer<typeof loginSchema>;

export default function Login() {
  const { toast } = useToast();
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const [needsVerification, setNeedsVerification] = useState(false);
  const [, setLocation] = useLocation();

  const form = useForm<LoginData>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      email: "",
      password: "",
      rememberMe: false,
    },
  });

  useEffect(() => {
    checkExistingAuth();
  }, []);

  const checkExistingAuth = async () => {
    const { session } = await getCurrentSession();
    if (session) {
      setLocation("/profile");
    }
  };

  const handleResendVerification = async () => {
    const email = form.getValues("email");
    if (!email) {
      toast({
        title: "Email Required",
        description: "Please enter your email address",
        variant: "destructive",
      });
      return;
    }

    const result = await resendVerificationEmail(email);
    if (result.success) {
      toast({
        title: "Email Sent",
        description: "Verification email has been sent. Please check your inbox.",
      });
    } else {
      toast({
        title: "Failed to Send",
        description: result.error || "Failed to send verification email",
        variant: "destructive",
      });
    }
  };

  const onSubmit = async (data: LoginData) => {
    setIsSubmitting(true);
    setErrorMessage("");
    setNeedsVerification(false);

    const result = await signIn(data.email, data.password);

    if (result.success && result.session) {
      toast({
        title: "Welcome back!",
        description: "You have been logged in successfully.",
      });
      setLocation("/profile");
    } else {
      setErrorMessage(result.error || "An unexpected error occurred");

      if (result.error?.toLowerCase().includes("verify your email") || 
          result.error?.toLowerCase().includes("email not confirmed")) {
        setNeedsVerification(true);
      }

      toast({
        title: "Login Failed",
        description: result.error || "Please check your credentials and try again",
        variant: "destructive",
      });
    }

    setIsSubmitting(false);
  };

  return (
    <div className="flex flex-col min-h-screen">
      <Navigation />
      <main className="flex-1 flex items-center justify-center py-16 px-4">
        <Card className="w-full max-w-md">
          <CardHeader className="text-center">
            <CardTitle className="text-2xl font-heading">Welcome Back</CardTitle>
            <CardDescription>Sign in to your Stootap account</CardDescription>
          </CardHeader>
          <CardContent>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                {errorMessage && (
                  <Alert variant="destructive">
                    <AlertCircle className="h-4 w-4" />
                    <AlertDescription>{errorMessage}</AlertDescription>
                  </Alert>
                )}

                {needsVerification && (
                  <Alert>
                    <AlertDescription className="flex items-center justify-between">
                      <span>Email not verified</span>
                      <Button
                        type="button"
                        variant="ghost"
                        size="sm"
                        onClick={handleResendVerification}
                        className="h-auto p-0 underline"
                      >
                        Resend verification
                      </Button>
                    </AlertDescription>
                  </Alert>
                )}

                <FormField
                  control={form.control}
                  name="email"
