        recentLeads: leads.slice(0, 10),
        ordersLast7Days: ordersLast7Days.length,
        ordersLast30Days: ordersLast30Days.length,
        leadsLast7Days: leadsLast7Days.length,
        leadsLast30Days: leadsLast30Days.length,
        revenueLast7Days: revenueLast7Days.toFixed(2),
        revenueLast30Days: revenueLast30Days.toFixed(2),
        weeklyTrend,
        servicePerformance,
        categoryStats,
        conversionRate,
        avgOrderValue,
        completionRate
      };

      res.json(analytics);
    } catch (error) {
      console.error("Analytics error:", error);
      res.status(500).json({ error: "Failed to fetch analytics" });
    }
  });

  app.get("/api/admin/orders", requireAdmin, async (_req, res) => {
    try {
      const orders = await storage.getAllOrders();
      res.json(orders);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch orders" });
    }
  });

  app.patch("/api/admin/orders/:id", requireAdmin, async (req, res) => {
    try {
      const { id } = req.params;
      const { status } = req.body;

      if (!status) {
        return res.status(400).json({ error: "Status is required" });
      }

      const order = await storage.updateOrder(id, { status });
      if (!order) {
        return res.status(404).json({ error: "Order not found" });
      }

      res.json(order);
    } catch (error) {
      res.status(500).json({ error: "Failed to update order" });
    }
  });

  app.get("/api/admin/orders/:id/details", requireAdmin, async (req, res) => {
    try {
      const { id } = req.params;
      const order = await storage.getOrder(id);

      if (!order) {
        return res.status(404).json({ error: "Order not found" });
      }

      const items = await storage.getOrderItemsByOrderId(id);

      res.json({
        ...order,
        items
      });
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch order details" });
    }
  });

  app.get("/api/admin/leads", requireAdmin, async (req, res) => {
    try {
      const leads = await storage.getAllLeads();

      // Support search and filtering
      let filteredLeads = leads;
      const { search, kind, role } = req.query;

      if (search) {
        const searchLower = (search as string).toLowerCase();
        filteredLeads = filteredLeads.filter(lead =>
          lead.name.toLowerCase().includes(searchLower) ||
          lead.email.toLowerCase().includes(searchLower) ||
          lead.phone.includes(searchLower) ||
          lead.message.toLowerCase().includes(searchLower)
        );
      }

      if (kind && kind !== 'all') {
        filteredLeads = filteredLeads.filter(lead => lead.kind === kind);
      }

      if (role && role !== 'all') {
        filteredLeads = filteredLeads.filter(lead => lead.role === role);
      }

      res.json(filteredLeads);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch leads" });
    }
  });

  app.get("/api/admin/leads/:id", requireAdmin, async (req, res) => {
    try {
      const { id } = req.params;
      const leads = await storage.getAllLeads();
      const lead = leads.find(l => l.id === id);

      if (!lead) {
        return res.status(404).json({ error: "Lead not found" });
      }

      res.json(lead);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch lead details" });
    }
  });

  // User Management APIs
  app.get("/api/admin/users", requireAdmin, async (_req, res) => {
    try {
      const { data } = await supabaseServer
        .from("profiles")
        .select("*")
        .order("created_at", { ascending: false });

      // Transform to camelCase
      const users = (data || []).map((user: any) => {
        return toCamelCase(user);
      });

      res.json(users);
    } catch (error) {
      console.error("Get users error:", error);
      res.status(500).json({ error: "Failed to fetch users" });
    }
  });

  app.post("/api/admin/users", requireAdmin, async (req, res) => {
    try {
      const userData = toSnakeCase(req.body);
      const { data, error } = await supabaseServer
        .from("profiles")
        .insert(userData)
        .select()
        .single();

      if (error) throw error;
      res.json(toCamelCase(data));
    } catch (error) {
      console.error("Create user error:", error);
      res.status(500).json({ 
        error: "Failed to create user",
        details: error instanceof Error ? error.message : String(error)
      });
    }
  });

  app.patch("/api/admin/users/:id", requireAdmin, async (req, res) => {
    try {
      const { id } = req.params;
      const updates = toSnakeCase(req.body);

      const { data, error } = await supabaseServer
        .from("profiles")
        .update(updates)
        .eq("id", id)
        .select()
        .single();

      if (error) throw error;
      if (!data) {
        return res.status(404).json({ error: "User not found" });
      }

      res.json(toCamelCase(data));
    } catch (error) {
      console.error("Update user error:", error);
      res.status(500).json({ error: "Failed to update user" });
    }
  });

  app.delete("/api/admin/users/:id", requireAdmin, async (req, res) => {
    try {
      const { id } = req.params;

      const { error } = await supabaseServer
        .from("profiles")
        .delete()
        .eq("id", id);

      if (error) throw error;
      res.json({ success: true });
    } catch (error) {
      console.error("Delete user error:", error);
      res.status(500).json({ error: "Failed to delete user" });
    }
  });

  // Notification/Message APIs
  app.post("/api/admin/notifications", requireAdmin, async (req, res) => {
    try {
      const { userId, title, message, type, actionUrl } = req.body;

      if (!userId || !title || !message) {
        return res.status(400).json({ error: "userId, title, and message are required" });
      }

      const notification = await storage.createNotification({
        userId,
        type: type || "admin_message",
        title,
        message,
        actionUrl: actionUrl || null,
      });

      res.json(notification);
    } catch (error) {
      console.error("Create notification error:", error);
      res.status(500).json({ error: "Failed to create notification" });
    }
  });

  app.get("/api/notifications", async (req, res) => {
    try {
      if (!req.user) {
        return res.status(401).json({ error: "Not authenticated" });
      }

      const notifications = await storage.getNotificationsByUserId(req.user.id);
      res.json(notifications);
    } catch (error) {
      console.error("Get notifications error:", error);
      res.status(500).json({ error: "Failed to fetch notifications" });
    }
  });

  app.patch("/api/notifications/:id/read", async (req, res) => {
    try {
      if (!req.user) {
        return res.status(401).json({ error: "Not authenticated" });
      }

      const notification = await storage.markNotificationAsRead(req.params.id);
      if (!notification) {
        return res.status(404).json({ error: "Notification not found" });
      }

      res.json(notification);
    } catch (error) {
