}
Calendar.displayName = "Calendar"

export { Calendar }

client/src/pages/JobDetail.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useParams, Link } from "wouter";
import { Navigation } from "@/components/Navigation";
import { Footer } from "@/components/Footer";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Checkbox } from "@/components/ui/checkbox";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { supabase } from "@/lib/supabase-client";
import { fetchWithAuth } from "@/lib/api-client";
import { 
  Briefcase, MapPin, Clock, Building2, Calendar, Users, 
  Star, IndianRupee, Globe, ArrowLeft, Heart, Share2, 
  CheckCircle, FileText, Loader2
} from "lucide-react";

interface JobPost {
  id: string;
  title: string;
  slug: string;
  roleType: string;
  isPaid: boolean;
  salaryMin: string | null;
  salaryMax: string | null;
  experienceLevel: string;
  locationType: string;
  city: string | null;
  workingDays: string | null;
  workingHours: string | null;
  isFlexible: boolean;
  description: string;
  responsibilities: string | null;
  requiredSkills: string[] | null;
  preferredQualifications: string | null;
  numberOfOpenings: number;
  applicationDeadline: string | null;
  visibility: string;
  createdAt: string;
  viewCount: number;
  company: {
    id: string;
    companyName: string;
    logoUrl: string | null;
    verified: boolean;
    city: string | null;
    state: string | null;
    description: string | null;
    websiteUrl: string | null;
  } | null;
}

export default function JobDetail() {
  const { slug } = useParams<{ slug: string }>();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [applyDialogOpen, setApplyDialogOpen] = useState(false);
  const [applicationData, setApplicationData] = useState({
    fullName: "",
    email: "",
    phone: "",
    collegeName: "",
    experienceSummary: "",
    preferredHours: "",
    cvUrl: "",
    coverNote: "",
    bankDetailsConsent: false,
  });

  const { data: job, isLoading } = useQuery<JobPost>({
    queryKey: ["/api/opportunities/jobs", slug],
    queryFn: async () => {
      const res = await fetch(`/api/opportunities/jobs/${slug}`);
      if (!res.ok) throw new Error("Job not found");
      return res.json();
    },
    enabled: !!slug,
  });

  const { data: user } = useQuery({
    queryKey: ["/api/me"],
    queryFn: async () => {
      const res = await fetch("/api/me");
      if (!res.ok) return null;
      return res.json();
    },
  });

  const { data: savedStatus } = useQuery<{ saved: boolean }>({
    queryKey: ["/api/opportunities/jobs", job?.id, "saved"],
    queryFn: async () => {
      const res = await fetchWithAuth(`/api/opportunities/jobs/${job?.id}/saved`);
      if (!res.ok) return { saved: false };
      return res.json();
    },
    enabled: !!job?.id && !!user,
  });

  const applyMutation = useMutation({
    mutationFn: async (data: typeof applicationData) => {
      const res = await fetchWithAuth(`/api/opportunities/jobs/${job?.id}/apply`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });
      if (!res.ok) {
        const error = await res.json();
        throw new Error(error.error || "Failed to submit application");
      }
      return res.json();
    },
    onSuccess: () => {
      toast({
        title: "Application Submitted",
        description: "Your application has been sent. The company will contact you if shortlisted.",
      });
      setApplyDialogOpen(false);
      queryClient.invalidateQueries({ queryKey: ["/api/opportunities/my-applications"] });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const saveJobMutation = useMutation({
    mutationFn: async () => {
      const res = await fetchWithAuth(`/api/opportunities/jobs/${job?.id}/save`, {
        method: savedStatus?.saved ? "DELETE" : "POST",
      });
      if (!res.ok) {
        const error = await res.json();
        throw new Error(error.error || "Failed to save job");
      }
      return res.json();
    },
    onSuccess: () => {
      toast({
        title: savedStatus?.saved ? "Removed from Saved" : "Job Saved",
        description: savedStatus?.saved ? "Job removed from your saved list." : "Job added to your saved list.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/opportunities/jobs", job?.id, "saved"] });
      queryClient.invalidateQueries({ queryKey: ["/api/opportunities/saved-jobs"] });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleSaveJob = () => {
    if (!user) {
      toast({
        title: "Sign In Required",
        description: "Please sign in to save jobs.",
        variant: "destructive",
      });
      return;
    }
    saveJobMutation.mutate();
  };

  const formatSalary = (min: string | null, max: string | null) => {
    if (!min && !max) return null;
    if (min && max) return `₹${parseInt(min).toLocaleString()} - ₹${parseInt(max).toLocaleString()}`;
    if (min) return `₹${parseInt(min).toLocaleString()}+`;
    return `Up to ₹${parseInt(max!).toLocaleString()}`;
  };

  const handleApply = (e: React.FormEvent) => {
    e.preventDefault();
    if (!applicationData.cvUrl) {
      toast({
        title: "CV Required",
        description: "Please upload your CV to apply.",
        variant: "destructive",
      });
      return;
    }
    applyMutation.mutate(applicationData);
  };

  const handleCvUpload = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    if (file.size > 5 * 1024 * 1024) {
      toast({
        title: "File too large",
        description: "CV must be less than 5MB",
        variant: "destructive",
      });
      return;
    }

    try {
      const fileName = `cv_${Date.now()}_${file.name}`;
      const { data, error } = await supabase.storage
        .from("cvs")
        .upload(fileName, file);

      if (error) throw error;

      const { data: urlData } = supabase.storage
        .from("cvs")
        .getPublicUrl(fileName);

      setApplicationData(prev => ({ ...prev, cvUrl: urlData.publicUrl }));
      toast({ title: "CV Uploaded", description: "Your CV has been uploaded successfully." });
    } catch (error) {
      toast({
        title: "Upload Failed",
        description: "Failed to upload CV. Please try again.",
        variant: "destructive",
      });
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Navigation />
        <main className="container mx-auto px-4 py-8">
          <div className="animate-pulse space-y-4">
            <div className="h-8 bg-gray-200 rounded w-1/3"></div>
            <div className="h-4 bg-gray-200 rounded w-1/4"></div>
            <div className="h-64 bg-gray-200 rounded"></div>
          </div>
        </main>
      </div>
    );
  }

  if (!job) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Navigation />
        <main className="container mx-auto px-4 py-16 text-center">
          <Briefcase className="h-16 w-16 text-gray-300 mx-auto mb-4" />
          <h1 className="text-2xl font-bold text-gray-900 mb-2">Job Not Found</h1>
          <p className="text-gray-600 mb-4">This opportunity may have been closed or removed.</p>
          <Link href="/opportunities">
            <Button>Browse Opportunities</Button>
          </Link>
        </main>
        <Footer />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Navigation />

      <main className="container mx-auto px-4 py-8">
        <Link href="/opportunities" className="inline-flex items-center text-gray-600 hover:text-gray-900 mb-6">
          <ArrowLeft className="h-4 w-4 mr-2" />
          Back to Opportunities
        </Link>

        <div className="grid lg:grid-cols-3 gap-8">
          <div className="lg:col-span-2 space-y-6">
            <Card>
              <CardContent className="p-8">
                <div className="flex items-start justify-between gap-4 mb-6">
                  <div className="flex items-start gap-4">
                    {job.company?.logoUrl ? (
                      <img
                        src={job.company.logoUrl}
                        alt={job.company.companyName}
                        className="w-16 h-16 rounded-xl object-cover"
                      />
                    ) : (
                      <div className="w-16 h-16 rounded-xl bg-gray-100 flex items-center justify-center">
                        <Building2 className="h-8 w-8 text-gray-400" />
                      </div>
                    )}
                    <div>
                      <h1 className="text-2xl font-bold text-gray-900">{job.title}</h1>
                      <div className="flex items-center gap-2 mt-1">
                        <span className="text-lg text-gray-600">{job.company?.companyName}</span>
                        {job.company?.verified && (
                          <Badge variant="secondary">
                            <CheckCircle className="h-3 w-3 mr-1" />
                            Verified
                          </Badge>
                        )}
                      </div>
                    </div>
                  </div>
                  <div className="flex gap-2">
                    <Button 
                      variant="outline" 
                      size="icon"
                      onClick={handleSaveJob}
                      disabled={saveJobMutation.isPending}
                      className={savedStatus?.saved ? "text-red-500 hover:text-red-600" : ""}
                    >
                      {saveJobMutation.isPending ? (
                        <Loader2 className="h-4 w-4 animate-spin" />
                      ) : (
                        <Heart className={`h-4 w-4 ${savedStatus?.saved ? "fill-current" : ""}`} />
                      )}
                    </Button>
                    <Button 
                      variant="outline" 
                      size="icon"
                      onClick={() => {
                        navigator.clipboard.writeText(window.location.href);
                        toast({ title: "Link Copied", description: "Job link copied to clipboard." });
                      }}
                    >
                      <Share2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>

                <div className="flex flex-wrap gap-2 mb-6">
                  <Badge className="bg-blue-100 text-blue-800 capitalize">{job.roleType}</Badge>
                  {job.visibility === "featured" && (
                    <Badge className="bg-blue-500 text-white">
                      <Star className="h-3 w-3 mr-1" />
                      Featured
                    </Badge>
                  )}
                  {job.isPaid && (
                    <Badge className="bg-blue-200 text-blue-900">
                      <IndianRupee className="h-3 w-3 mr-1" />
                      Paid Opportunity
                    </Badge>
                  )}
                  {job.isFlexible && (
                    <Badge variant="outline">Flexible Hours</Badge>
                  )}
                </div>

                <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6">
                  <div className="flex items-center gap-2 text-gray-600">
                    <MapPin className="h-5 w-5" />
                    <span>{job.locationType === "remote" ? "Remote" : job.city || job.locationType}</span>
                  </div>
                  <div className="flex items-center gap-2 text-gray-600">
                    <Briefcase className="h-5 w-5" />
                    <span>{job.experienceLevel}</span>
                  </div>
                  {job.workingHours && (
                    <div className="flex items-center gap-2 text-gray-600">
                      <Clock className="h-5 w-5" />
                      <span>{job.workingHours}</span>
                    </div>
                  )}
                  {job.numberOfOpenings > 1 && (
                    <div className="flex items-center gap-2 text-gray-600">
                      <Users className="h-5 w-5" />
                      <span>{job.numberOfOpenings} openings</span>
                    </div>
                  )}
                </div>

                {formatSalary(job.salaryMin, job.salaryMax) && (
                  <div className="p-4 bg-blue-50 rounded-lg mb-6">
                    <p className="text-lg font-semibold text-blue-700">
                      {formatSalary(job.salaryMin, job.salaryMax)}/month
                    </p>
                  </div>
                )}

                <div className="prose max-w-none">
                  <h3 className="text-lg font-semibold mb-3">Job Description</h3>
                  <p className="text-gray-700 whitespace-pre-wrap">{job.description}</p>

                  {job.responsibilities && (
                    <>
                      <h3 className="text-lg font-semibold mt-6 mb-3">Responsibilities</h3>
                      <p className="text-gray-700 whitespace-pre-wrap">{job.responsibilities}</p>
                    </>
                  )}

                  {job.requiredSkills && job.requiredSkills.length > 0 && (
                    <>
                      <h3 className="text-lg font-semibold mt-6 mb-3">Required Skills</h3>
                      <div className="flex flex-wrap gap-2">
                        {job.requiredSkills.map((skill, index) => (
                          <Badge key={index} variant="outline">{skill}</Badge>
                        ))}
                      </div>
                    </>
                  )}

                  {job.preferredQualifications && (
                    <>
                      <h3 className="text-lg font-semibold mt-6 mb-3">Preferred Qualifications</h3>
                      <p className="text-gray-700 whitespace-pre-wrap">{job.preferredQualifications}</p>
                    </>
                  )}
                </div>
              </CardContent>
            </Card>
          </div>

          <div className="space-y-6">
            <Card className="sticky top-24">
              <CardContent className="p-6">
                <Dialog open={applyDialogOpen} onOpenChange={setApplyDialogOpen}>
                  <DialogTrigger asChild>
                    <Button className="w-full mb-4" size="lg">
                      Apply Now
                    </Button>
                  </DialogTrigger>
                  <DialogContent className="max-w-lg max-h-[90vh] overflow-y-auto">
                    <DialogHeader>
                      <DialogTitle>Apply for {job.title}</DialogTitle>
                      <DialogDescription>
                        at {job.company?.companyName}
                      </DialogDescription>
                    </DialogHeader>
                    <form onSubmit={handleApply} className="space-y-4 mt-4">
                      <div>
                        <Label htmlFor="fullName">Full Name *</Label>
                        <Input
                          id="fullName"
                          value={applicationData.fullName}
                          onChange={(e) => setApplicationData(prev => ({ ...prev, fullName: e.target.value }))}
                          required
                        />
                      </div>
                      <div>
                        <Label htmlFor="email">Email *</Label>
                        <Input
                          id="email"
                          type="email"
                          value={applicationData.email}
                          onChange={(e) => setApplicationData(prev => ({ ...prev, email: e.target.value }))}
                          required
                        />
                      </div>
                      <div>
                        <Label htmlFor="phone">Phone *</Label>
                        <Input
                          id="phone"
                          type="tel"
                          value={applicationData.phone}
                          onChange={(e) => setApplicationData(prev => ({ ...prev, phone: e.target.value }))}
                          required
                        />
                      </div>
                      <div>
                        <Label htmlFor="collegeName">College Name (optional)</Label>
                        <Input
                          id="collegeName"
                          value={applicationData.collegeName}
                          onChange={(e) => setApplicationData(prev => ({ ...prev, collegeName: e.target.value }))}
                        />
                      </div>
                      <div>
                        <Label htmlFor="experienceSummary">Experience Summary (optional)</Label>
                        <Textarea
                          id="experienceSummary"
                          value={applicationData.experienceSummary}
                          onChange={(e) => setApplicationData(prev => ({ ...prev, experienceSummary: e.target.value }))}
                          rows={3}
                        />
                      </div>
                      <div>
                        <Label htmlFor="cv">Upload CV * (PDF preferred, max 5MB)</Label>
                        <Input
                          id="cv"
                          type="file"
                          accept=".pdf,.doc,.docx"
                          onChange={handleCvUpload}
                          className="mt-1"
                        />
                        {applicationData.cvUrl && (
                          <p className="text-sm text-green-600 mt-1 flex items-center gap-1">
                            <CheckCircle className="h-4 w-4" />
                            CV uploaded successfully
                          </p>
                        )}
                      </div>
                      <div>
                        <Label htmlFor="coverNote">Cover Note (optional)</Label>
                        <Textarea
                          id="coverNote"
                          value={applicationData.coverNote}
                          onChange={(e) => setApplicationData(prev => ({ ...prev, coverNote: e.target.value }))}
                          rows={4}
                          placeholder="Why are you interested in this role?"
                        />
                      </div>

                      <div className="flex items-start gap-2">
                        <Checkbox
                          id="consent"
                          required
                        />
                        <Label htmlFor="consent" className="text-sm text-gray-600 leading-tight">
                          I consent to share my data with the employer and agree to the platform's privacy policy.
                        </Label>
                      </div>

                      <Button 
                        type="submit" 
                        className="w-full"
                        disabled={applyMutation.isPending}
                      >
                        {applyMutation.isPending ? "Submitting..." : "Submit Application"}
                      </Button>
                    </form>
                  </DialogContent>
                </Dialog>

                {job.applicationDeadline && (
                  <p className="text-sm text-gray-500 text-center mb-4">
                    <Calendar className="h-4 w-4 inline mr-1" />
                    Apply by {new Date(job.applicationDeadline).toLocaleDateString()}
                  </p>
                )}

                <p className="text-sm text-gray-500 text-center">
                  {job.viewCount} people viewed this job
                </p>
              </CardContent>
            </Card>

            {job.company && (
              <Card>
                <CardHeader>
                  <CardTitle className="text-lg">About {job.company.companyName}</CardTitle>
                </CardHeader>
                <CardContent className="space-y-3">
                  {job.company.description && (
                    <p className="text-gray-600 text-sm">{job.company.description}</p>
                  )}
                  <div className="flex items-center gap-2 text-sm text-gray-500">
                    <MapPin className="h-4 w-4" />
                    {job.company.city}, {job.company.state}
                  </div>
                  {job.company.websiteUrl && (
                    <a
                      href={job.company.websiteUrl}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="flex items-center gap-2 text-sm text-primary hover:underline"
                    >
                      <Globe className="h-4 w-4" />
                      Visit Website
                    </a>
                  )}
                </CardContent>
              </Card>
            )}
          </div>
        </div>
      </main>

      <Footer />
    </div>
  );
}

pages/Checkout.tsx.content:
import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Separator } from "@/components/ui/separator";
import Navigation from "@/components/Navigation";
import Footer from "@/components/Footer";
import { ShoppingBag, CheckCircle } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

declare global {
  interface Window {
    Razorpay: any;
  }
}

export default function Checkout() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [cartItems, setCartItems] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState("");
  const [razorpayLoaded, setRazorpayLoaded] = useState(false);

  const [formData, setFormData] = useState({
    customerName: "",
    customerEmail: "",
    customerPhone: "",
    customerAddress: "",
    companyName: "",
    gstNumber: "",
    city: "",
    state: "",
    pincode: "",
  });

  const sessionId = localStorage.getItem("sessionId") || "";

  useEffect(() => {
    loadCart();
    loadRazorpayScript();
  }, []);

  const loadRazorpayScript = () => {
    if (window.Razorpay) {
      setRazorpayLoaded(true);
      return;
    }

    const script = document.createElement("script");
    script.src = "https://checkout.razorpay.com/v1/checkout.js";
    script.async = true;
    script.onload = () => setRazorpayLoaded(true);
    script.onerror = () => {
      console.error("Failed to load Razorpay script");
      toast({
        title: "Payment System Unavailable",
        description: "Please try again later or contact support.",
        variant: "destructive",
      });
    };
    document.body.appendChild(script);
  };

  const loadCart = async () => {
    if (!sessionId) {
      setLoading(false);
      return;
    }

    try {
      const response = await fetch(`/api/cart/${sessionId}`);
      if (response.ok) {
        const data = await response.json();
        setCartItems(data);
      }
    } catch (err) {
      console.error("Failed to load cart", err);
    } finally {
      setLoading(false);
    }
  };

  const calculateTotals = () => {
    const subtotal = cartItems.reduce((sum, item) => {
      return sum + (parseFloat(item.service?.basePriceInr || 0) * item.qty);
    }, 0);
    const gst = subtotal * 0.18;
    const total = subtotal + gst;
    return { subtotal, gst, total };
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError("");

    if (!razorpayLoaded) {
      setError("Payment system is still loading. Please try again.");
      return;
    }

    if (cartItems.length === 0) {
      setError("Your cart is empty. Please add services before checking out.");
      return;
    }

    // Validate cart items have service data
    const invalidItems = cartItems.filter(item => !item.service || !item.service.basePriceInr);
    if (invalidItems.length > 0) {
      setError("Some items in your cart are invalid. Please refresh the page and try again.");
      return;
    }

    setSubmitting(true);

    const { subtotal, gst, total } = calculateTotals();

    // Build full address
    const fullAddress = [
      formData.customerAddress,
      formData.city,
      formData.state,
      formData.pincode
    ].filter(Boolean).join(', ');

    const orderItems = cartItems.map(item => ({
      serviceId: item.serviceId,
      name: item.service?.name || "Service",
      unitPriceInr: item.service?.basePriceInr || "0",
      qty: item.qty,
      totalInr: (parseFloat(item.service?.basePriceInr || 0) * item.qty).toFixed(2),
    }));

    try {
      const orderResponse = await fetch("/api/orders", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          sessionId,
          status: "payment_processing",
          subtotalInr: subtotal.toFixed(2),
          gstInr: gst.toFixed(2),
          totalInr: total.toFixed(2),
          customerName: formData.customerName,
          customerEmail: formData.customerEmail,
          customerPhone: formData.customerPhone,
          customerAddress: fullAddress,
          items: orderItems,
        }),
      });

      if (!orderResponse.ok) {
        const data = await orderResponse.json();
        throw new Error(data.error || "Failed to create order");
      }

      const order = await orderResponse.json();

      // Check if payment gateway is available
      const razorpayKeyResponse = await fetch("/api/payment/razorpay-key");

      if (!razorpayKeyResponse.ok || razorpayKeyResponse.status === 503) {
        // Payment gateway is disabled - order is already created, clear cart and redirect
        console.log("Payment gateway disabled, order created:", order.id);

        await fetch("/api/cart/clear", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ sessionId }),
        });

        toast({
          title: "Order Created Successfully",
          description: `Your order (ID: ${order.id.slice(0, 8)}) has been created. Our team will contact you shortly to complete the payment process.`,
          duration: 6000,
        });

        setSuccess(true);
        setTimeout(() => setLocation("/profile"), 2000);
        return;
      }

      const { key } = await razorpayKeyResponse.json();

      const razorpayOrderResponse = await fetch("/api/payment/create-order", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          amount: parseFloat(total.toFixed(2)),
          currency: "INR",
          receipt: order.id,
          notes: {
            orderId: order.id,
            customerName: formData.customerName,
            customerEmail: formData.customerEmail,
            companyName: formData.companyName || "N/A",
            gstNumber: formData.gstNumber || "N/A",
            phone: formData.customerPhone,
          },
        }),
      });

      if (!razorpayOrderResponse.ok) {
        const errorData = await razorpayOrderResponse.json();
        throw new Error(errorData.details || errorData.error || "Failed to create payment order");
      }

      const razorpayOrder = await razorpayOrderResponse.json();

      const options = {
        key,
        amount: razorpayOrder.amount,
        currency: razorpayOrder.currency,
        name: "Stootap",
        description: "Business Services Payment",
        order_id: razorpayOrder.id,
        handler: async function (response: any) {
          try {
            const verifyResponse = await fetch("/api/payment/verify", {
              method: "POST",
              headers: { "Content-Type": "application/json" },
              body: JSON.stringify({
                razorpay_order_id: response.razorpay_order_id,
                razorpay_payment_id: response.razorpay_payment_id,
                razorpay_signature: response.razorpay_signature,
                orderId: order.id,
              }),
            });

            if (!verifyResponse.ok) {
              throw new Error("Payment verification failed");
            }

            await fetch("/api/cart/clear", {
              method: "POST",
              headers: { "Content-Type": "application/json" },
              body: JSON.stringify({ sessionId }),
            });

            setSuccess(true);
            setTimeout(() => setLocation("/"), 3000);
          } catch (err: any) {
            console.error("Payment verification error:", err);
            setError("Payment verification failed. Please contact support with your order ID: " + order.id);
            setSubmitting(false);
          }
        },
        prefill: {
          name: formData.customerName,
          email: formData.customerEmail,
          contact: formData.customerPhone,
        },
        notes: {
          orderId: order.id,
        },
        theme: {
          color: "#6366f1",
        },
        modal: {
          ondismiss: function () {
            toast({
              title: "Payment Cancelled",
              description: `Your order ${order.id} has been created and saved. You can complete payment later by contacting support.`,
              variant: "default",
            });

            fetch("/api/cart/clear", {
              method: "POST",
              headers: { "Content-Type": "application/json" },
              body: JSON.stringify({ sessionId }),
            });

            setSuccess(true);
            setTimeout(() => setLocation("/profile"), 2000);
          },
          onhidden: function() {
            setSubmitting(false);
          }
        },
      };

      const paymentObject = new window.Razorpay(options);

      paymentObject.on('payment.failed', async function (response: any){
        console.log("Payment failed:", response);
        toast({
          title: "Payment Failed",
          description: `Your order ${order.id} has been saved. Please contact support to complete payment.`,
          variant: "destructive",
        });

        await fetch("/api/cart/clear", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ sessionId }),
        });

        setSuccess(true);
        setTimeout(() => setLocation("/profile"), 2000);
      });

      paymentObject.open();
    } catch (err: any) {
      console.error("Checkout error:", err);
      const errorMessage = err.message || err.error || "An error occurred. Please check your details and try again.";
      setError(errorMessage);
      toast({
        title: "Checkout Error",
        description: errorMessage,
        variant: "destructive",
      });
      setSubmitting(false);
    }
  };

  const { subtotal, gst, total } = calculateTotals();

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="w-16 h-16 border-4 border-primary border-t-transparent rounded-full animate-spin"></div>
      </div>
    );
  }

  if (success) {
    return (
      <div className="min-h-screen bg-slate-50 dark:bg-slate-950">
        <Navigation />
        <div className="min-h-[80vh] flex items-center justify-center px-4">
          <Card className="max-w-md w-full text-center">
            <CardContent className="pt-8 pb-8">
              <CheckCircle className="w-16 h-16 text-green-500 mx-auto mb-4" />
              <h2 className="text-2xl font-bold mb-2">Order Placed Successfully!</h2>
              <p className="text-muted-foreground mb-4">
                Thank you for your order. We'll be in touch soon.
              </p>
              <p className="text-sm text-muted-foreground">
                Redirecting to home page...
              </p>
            </CardContent>
          </Card>
        </div>
        <Footer />
      </div>
    );
  }

  if (cartItems.length === 0) {
    return (
      <div className="min-h-screen bg-slate-50 dark:bg-slate-950">
        <Navigation />
        <div className="min-h-[80vh] flex items-center justify-center px-4">
          <Card className="max-w-md w-full text-center">
            <CardContent className="pt-8 pb-8">
              <ShoppingBag className="w-16 h-16 text-muted-foreground mx-auto mb-4" />
              <h2 className="text-2xl font-bold mb-2">Your Cart is Empty</h2>
              <p className="text-muted-foreground mb-4">
                Add some services to your cart before checking out.
              </p>
              <Button onClick={() => setLocation("/services")}>
                Browse Services
              </Button>
            </CardContent>
          </Card>
        </div>
        <Footer />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-slate-50 dark:bg-slate-950">
      <Navigation />
      <main className="max-w-7xl mx-auto px-4 lg:px-8 py-12">
        <h1 className="text-3xl lg:text-4xl font-bold mb-8">Checkout</h1>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          <div className="lg:col-span-2">
            <Card>
              <CardHeader>
                <CardTitle>Customer Information</CardTitle>
                <CardDescription>
                  Please provide your details to complete the order
                </CardDescription>
              </CardHeader>
              <CardContent>
                <form onSubmit={handleSubmit} className="space-y-4">
                  {error && (
                    <Alert variant="destructive">
                      <AlertDescription>{error}</AlertDescription>
                    </Alert>
                  )}

                  <div className="space-y-2">
                    <Label htmlFor="name">Full Name *</Label>
                    <Input
                      id="name"
                      type="text"
                      required
                      value={formData.customerName}
                      onChange={(e) => setFormData({ ...formData, customerName: e.target.value })}
                      placeholder="John Doe"
                    />
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="email">Email Address *</Label>
                    <Input
                      id="email"
                      type="email"
                      required
                      value={formData.customerEmail}
                      onChange={(e) => setFormData({ ...formData, customerEmail: e.target.value })}
                      placeholder="john@example.com"
                    />
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="phone">Phone Number *</Label>
                    <Input
                      id="phone"
                      type="tel"
                      required
                      value={formData.customerPhone}
                      onChange={(e) => setFormData({ ...formData, customerPhone: e.target.value })}
                      placeholder="+91 98765 43210"
                    />
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="company">Company Name</Label>
                    <Input
                      id="company"
                      type="text"
                      value={formData.companyName}
                      onChange={(e) => setFormData({ ...formData, companyName: e.target.value })}
                      placeholder="Your Company Pvt. Ltd."
                    />
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="gst">GST Number (Optional)</Label>
                    <Input
                      id="gst"
                      type="text"
                      value={formData.gstNumber}
                      onChange={(e) => setFormData({ ...formData, gstNumber: e.target.value.toUpperCase() })}
                      placeholder="22AAAAA0000A1Z5"
                      maxLength={15}
                    />
                    <p className="text-xs text-muted-foreground">Enter if you have GST registration for tax credit</p>
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="address">Street Address *</Label>
                    <Textarea
                      id="address"
                      required
                      value={formData.customerAddress}
                      onChange={(e) => setFormData({ ...formData, customerAddress: e.target.value })}
                      placeholder="Building number, street name, area"
                      rows={2}
                    />
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    <div className="space-y-2">
                      <Label htmlFor="city">City *</Label>
                      <Input
                        id="city"
                        type="text"
                        required
                        value={formData.city}
                        onChange={(e) => setFormData({ ...formData, city: e.target.value })}
                        placeholder="Mumbai"
                      />
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="state">State *</Label>
                      <Input
                        id="state"
                        type="text"
                        required
                        value={formData.state}
                        onChange={(e) => setFormData({ ...formData, state: e.target.value })}
                        placeholder="Maharashtra"
                      />
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="pincode">PIN Code *</Label>
                      <Input
                        id="pincode"
                        type="text"
                        required
                        value={formData.pincode}
                        onChange={(e) => setFormData({ ...formData, pincode: e.target.value })}
                        placeholder="400001"
                        maxLength={6}
                        pattern="[0-9]{6}"
                      />
                    </div>
                  </div>

                  <div className="pt-4 border-t">
                    <div className="flex items-center gap-2 text-sm text-muted-foreground mb-4">
                      <input type="checkbox" id="terms" required className="h-4 w-4 rounded border-gray-300" />
                      <label htmlFor="terms">
                        I agree to the <a href="/terms" className="text-primary underline">Terms & Conditions</a> and <a href="/privacy" className="text-primary underline">Privacy Policy</a>
                      </label>
                    </div>
                  </div>

                  <Button
                    type="submit"
                    className="w-full"
                    disabled={submitting || !razorpayLoaded}
                    size="lg"
                  >
                    {!razorpayLoaded ? "Loading Payment System..." : submitting ? "Placing Order..." : `Proceed to Payment - ₹${total.toFixed(2)}`}
                  </Button>

                  {!razorpayLoaded && (
                    <p className="text-xs text-center text-muted-foreground">
                      Secure payment powered by Razorpay
                    </p>
                  )}
                </form>
              </CardContent>
            </Card>
          </div>

          <div>
            <Card>
              <CardHeader>
                <CardTitle>Order Summary</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="space-y-3">
                  {cartItems.map((item) => (
                    <div key={item.id} className="flex justify-between text-sm">
                      <span className="flex-1">
                        {item.service?.name} × {item.qty}
                      </span>
                      <span className="font-medium">
                        ₹{(parseFloat(item.service?.basePriceInr || 0) * item.qty).toFixed(2)}
                      </span>
                    </div>
                  ))}
                </div>

                <Separator />

                <div className="space-y-2">
                  <div className="flex justify-between text-sm">
                    <span>Subtotal</span>
                    <span>₹{subtotal.toFixed(2)}</span>
                  </div>
                  <div className="flex justify-between text-sm">
                    <span>GST (18%)</span>
                    <span>₹{gst.toFixed(2)}</span>
                  </div>

                  <Separator />

                  <div className="flex justify-between text-lg font-bold">
                    <span>Total</span>
                    <span>₹{total.toFixed(2)}</span>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>
        </div>
      </main>
      <Footer />
    </div>
  );
}

client/src/components/ui/textarea.tsx.content:
import * as React from "react"

import { cn } from "@/lib/utils"

const Textarea = React.forwardRef<
  HTMLTextAreaElement,
  React.ComponentProps<"textarea">
>(({ className, ...props }, ref) => {
  return (
    <textarea
      className={cn(
        "flex min-h-[80px] w-full rounded-md border border-input bg-background px-3 py-2 text-base ring-offset-background placeholder:text-muted-foreground focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50 md:text-sm",
        className
